mapper.update:
UPDATE User u
SET password = #{password},
    updated_at = #{updatedAt}
WHERE user_id = #{userId}
  AND (u.is_deleted = FALSE OR u.is_deleted IS NULL)

mapper.select:
SELECT IFNULL(main_bank, '') FROM user u
where user_id=#{userId}
  AND (u.is_deleted = FALSE OR u.is_deleted IS NULL)

select birth_date from user u
where user_id=#{userId}
  AND (u.is_deleted = FALSE OR u.is_deleted IS NULL)

mapper.update:
update user u
set main_bank=#{bankCode}
where user_id=#{userId}
  AND (u.is_deleted = FALSE OR u.is_deleted IS NULL)
